import React, { Component } from 'react';

import classes from './Tile.module.css';

class Tile extends Component {

    calcPositionStyles = (x, y) => {
        return {
            left: `${this.props.length*0.1*x}px`,
            top: `${this.props.length*0.1*y}px`
        }
    }

    render(){
        const x = this.props.x;
        const y = this.props.y;
        const highlighted = this.props.highlighted; 

        let cssClass = null;

        if((x+y)%2 === 1){
            //dark
            if(highlighted.filter(pos => {
                if(pos[0] === x && pos[1] === y){return true} else{return false}}).length>1){
                //selected
                cssClass = classes.SelectedDark;
            }else {
                //not selected
                cssClass = classes.Dark;
            }
        }else {
            if(highlighted.filter(pos => {
                if(pos[0] === x && pos[1] === y){return true} else{return false}}).length>1){
                //selected
                cssClass = classes.SelectedLight;
            }else {
                //not selected
                cssClass = classes.Light;
            }
        }





        return <div className={cssClass} style={this.calcPositionStyles(x,y)}>{x},{y}</div>
    }
}
export default Tile;
