import React, { Component } from 'react';
import classes from './Deathstacks.module.css';
import Board from './Board/Board';
import Tower from './Tower/Tower';

const LENGTH = 800;

class Deathstacks extends Component {

    state = {
        towers: [
            ['rr','rr','rr','rr','rr','rr','rr','rr','rr','rr'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            [ '_', '_', '_', '_', '_', '_', '_', '_', '_', '_'],
            ['bb','bb','bb','bb','bb','bb','bb','bb','bb','bb']
        ]
    }


    render(){
        let towers = [];
        for(let row=0;row<10;row++){
            for(let col=0;col<10;col++){
                if(this.state.towers[row][col] != '_'){
                    towers.push(<Tower x={col} y={row} boardLength={LENGTH} stones={this.state.towers[row][col]}/>);
                }
            }
        }
        return (
            <div className={classes.Deathstacks}>
                <Board length={LENGTH}/>

                <Tower x={4} y={5} boardLength={LENGTH} stones={'rrbbbr'}/>
            </div>
        )
    }
}
export default Deathstacks;
