import React, { Component } from 'react';
import BlueStone from './BlueStone.png';
import RedStone from './RedStone.png';

class Tower extends Component {







    render(){
        const stones = this.props.stones.split(';').map(x => {
            if(x === 'red') {return <img src={RedStone} alt='red Stone'/>}
            else {return <img src={BlueStone} alt='blue stone'/>}
        });

        return(
            <div className={classes.tower}>
                {stones}
            </div>
        )
    }
}
export default Tower;
