import React, { Component } from 'react';
import classes from './Board.module.css';

const LENGTH = 800;

class Board extends Component {

    calcPositionStyles = (x, y) => {
        return {
            left: `${LENGTH*0.1*x}px`,
            top: `${LENGTH*0.1*y}px`
        }
    }

    render() {
        console.log('highlighted', this.props.highlighted);
        let tiles = [];
        for(let i=0;i<10;i++){
            for(let j=0;j<10;j++){
                const cssClasses = [classes.Tile];
                if((i+j)%2 === 1) {
                    console.log('dark [',[i,j],']');
                    if(this.props.highlighted.filter(
                        (x,y) => {
                            if(x === i && y === j) return true 
                            return false
                        }
                    ).length > 0){
                        cssClasses.push(classes.SelectedDarkTile);
                    } else {
                        cssClasses.push(classes.DarkTile);
                    }
                } else {
                    if(this.props.highlighted.includes([i,j])){
                        cssClasses.push(classes.SelectedLightTile);
                    } else {
                        cssClasses.push(classes.LightTile);
                    }
                }
                tiles.push(<div style={this.calcPositionStyles(i, j)} className={cssClasses.join(' ')} key={[i,j]}>{i},{j}</div>);

            }
        }
        return(
            <div className={classes.Board}>
                {tiles}
            </div>
        )
    }
}

export default Board;
