import React, { Component } from 'react';

import classes from './Tile.module.css';

class Tile extends Component {

    calcPositionStyles = (x, y) => {
        return {
            left: `${this.props.length*0.1*x}px`,
            top: `${this.props.length*0.1*y}px`
        }
    }

    render(){
        const x = this.props.x;
        const y = this.props.y;
        const highlighted = this.props.highlighted.split(';');
        console.log('highlighted', highlighted); 

        let cssClasses = [classes.Tile];

        if((x+y)%2 === 1){
            //dark
            if(highlighted.contains(`${x}${y}`)){
                //selected
                cssClasses.push(classes.SelectedDark);
            }else {
                //not selected
                cssClasses.push(classes.Dark);
            }
        }else {
            if(highlighted.contains(`${x}${y}`)){
                    //selected
                cssClasses.push(classes.SelectedLight);
            }else {
                //not selected
                cssClasses.push(classes.Light);
            }
        }

        return <div className={cssClasses.join(' ')} style={this.calcPositionStyles(x,y)}>{x},{y}</div>
    }
}
export default Tile;
