        ],
        clicked: null
    }

    calcHighlighted = (posX, posY) => {




        return [[1,2],[4,8],[7,4]]
    }


    getPossibleMoves = (x ,y) => {
        const tower = this.state.towers[x][y].split('');
        const possibleTargets = [];

        // for(tower.length)


        return possibleTargets.join(';');
    }

    render(){
        let towers = [];
        for(let row=0;row<10;row++){
            for(let col=0;col<10;col++){
                if(this.state.towers[row][col] !== '_'){
                    towers.push(<Tower 
                                    clicked={this.state.towers[row][col].clicked} 
                                    x={col} 
                                    y={row} 
                                    boardLength={LENGTH} 
                                    stones={this.state.towers[row][col]}
                                    key={[col, row]}/>);
                }
            }
        }
        console.log(this.getPossibleMoves(0,2));
        return (
            <div className={classes.Deathstacks}>
                <Board highlighted={"12;44"} length={LENGTH}/>
                {towers}
            </div>
        )
    }
}
export default Deathstacks;
